/* ë¼ëì¤/ì²´í¬ë°ì¤ */
function chkBox(){
	$('input[type=radio][class^=styled], input[type=checkbox][class^=styled]').each(function(){
		if(!$(this).closest('span').hasClass('chkBox')){
			$(this).wrap('<span class="chkBox"></span>');
			$(this).closest('.chkBox').append('<span class="chk"></span>');
		}
	});
}

/* í¤ë fixed */
function header_fixed() {
	var headerPos = $('.slider-nav .txt').outerHeight();
	var window = $(this).scrollTop();		
//	console.log(headerPos);
	if(headerPos <= window) {
		$(".header").addClass("fixed");
	} else {
		$(".header").removeClass("fixed");
	}
}

/* ì«ìì¹´ì´í */
function counting(){
	$('.counter').each(function(){
		$(this).counterUp({
			delay: 10,
			time: 700
		});
	});
}

/* progress bar */
function progress(){
	$(".progress-bar > .myBar").each(function() {
		$(this)
			.data("origWidth", $(this).width())
			.width(0)
			.animate({
				width: $(this).data("origWidth")
			}, 1000);
	});
}

 /* circle procgess */
 function progressCircle(){
	$('.counting').each(function(){
		var val = $(this).find('strong').attr('data');
		
		$(this).find('.type1').circleProgress({
			startAngle: -Math.PI / 4 * 2,
			size:58,
			  value: val,
			  thickness:3,
			  lineCap: 'round',
			  fill: {color: '#109cf2'}, 
		}).on('circle-animation-progress', function(event, progress, stepValue) {
			$(this).find('strong').html(Math.round(100 * stepValue) + '<i>%</i>');
		});
		$(this).find('.type2').circleProgress({
			startAngle: -Math.PI / 4 * 2,
			size:58,
			  value: val,
			  thickness:3,
			  lineCap: 'round',
			  fill: {color: '#e73594'}
		}).on('circle-animation-progress', function(event, progress, stepValue) {
			$(this).find('strong').html(Math.round(100 * stepValue) + '<i>%</i>');
		});
		$(this).find('.type3').circleProgress({
			startAngle: -Math.PI / 4 * 2,
			size:58,
			  value: val,
			  thickness:3,
			  lineCap: 'round',
			  fill: {color: '#888'}
		}).on('circle-animation-progress', function(event, progress, stepValue) {
			$(this).find('strong').html(Math.round(100 * stepValue) + '<i>%</i>');
		});
	  });
 }

